import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import '../style/newCart.css'
const ProductSubtotal = () => {
  let price = 1789
  const [qtyIncrease, setQtyIncrease] = useState({
    counter: '1',
    subtotal: function () {
      return (parseInt(this.counter) * price).toString()
    },
  })
  const qtyHandlerPlus = () => {
    let numberCnvrt = parseInt(qtyIncrease.counter)
    numberCnvrt = numberCnvrt + 1
    setQtyIncrease({
      counter: numberCnvrt.toString(),
      subtotal: function () {
        return (parseInt(this.counter) * price).toString()
      },
    })
  }
  const qtyHandlerMinus = () => {
    let numberCnvrt = parseInt(qtyIncrease.counter)
    numberCnvrt = numberCnvrt - 1
    if (numberCnvrt > 0) {
      setQtyIncrease({
        counter: numberCnvrt.toString(),
        subtotal: function () {
          return (parseInt(this.counter) * price).toString()
        },
      })
    }
  }
  const qtyHandler = () => {
    qtyHandlerPlus()
    qtyHandlerMinus()
  }
  return (
    <div>
      <Container fluid className='container'>
        <Row className='row'>
          <Col xs={3} md sm lg={6}>
            Item
          </Col>
          <Col xs>Price</Col>
          <Col xs>Qty</Col>
          <Col xs>Subtotal</Col>
        </Row>
        <Row className='row'>
          <Col xs={3} md sm lg={6}>
            <Row className='productDescription'>
              <h4 className='productName'>Product Name This is the Product</h4>
              <p>Company: Pfizer</p>
              <p>Per Tablet: 1gm</p>
              <p>Tablets :10</p>
            </Row>
          </Col>
          <Col xs>
            <div>
              <p style={{ fontSize: '14px' }}>PKR 1789.0</p>
            </div>
          </Col>
          <Col xs>
            <button onClick={qtyHandlerMinus} className='qtyMinus'>
              <i className='fa fa-minus' aria-hidden='true'></i>
            </button>
            <input
              onChange={qtyHandler}
              className='qtyBox'
              value={qtyIncrease.counter}></input>
            <button onClick={qtyHandlerPlus} className='qtyPlus'>
              <i className='fa fa-plus' aria-hidden='true'></i>
            </button>
          </Col>
        </Row>
      </Container>
    </div>
  )
}

export default ProductSubtotal
