//import Fab from '@material-ui/core/Fab'
import React, { useState } from 'react'
import '../style/index.css'

const Header = () => {
  const [styleState, setStyleState] = useState({
    style: {
      display: 'flex',
      height: '80px',
      flexDirection: 'row',
      alignItems: 'flex-start',
      justifyContent: 'center',
      paddingTop: '15px',
      paddingBottom: '10px',
      backgroundColor: 'rgb(218, 212, 212)',
      transitionProperty: 'height',
      transitionDuration: '1s',
    },
    searchButtonStyle: {
      alignSelf: 'flex-start',
      justifySelf: 'flex-start',
      marginRigth: '20px',
    },
    searchBoxStyle: {
      marginRight: '30px',
    },
  })
  const searchBarCloseHandler = () => {
    setStyleState({
      style: {
        //display: 'none',
        height: '0px',
      },
    })
  }
  return (
    <div>
      <div style={styleState.style}>
        <form action=''>
          <input style={styleState.searchBoxStyle} type='text' size='50' />
          <button style={styleState.searchButtonStyle}>Search</button>
        </form>
        <div onClick={searchBarCloseHandler}>
          <i class='fa fa-times' aria-hidden='true' />
        </div>
      </div>
    </div>
  )
}

export default Header
